package tutoj8.streams;import java.util.ArrayList;import java.util.stream.Stream;/** * Created by Oana on 24.04.2018. */public class MainAdvancedStream {    public static void main(String[] args) {        lazyStream();        threeDigit();        chaninigOptional();            }    private static void threeDigit() {    }    private static void chaninigOptional() {    }    private static void lazyStream() {        System.out.println("MainAdvancedStream.lazyStream> a Stream is not created when we make list.stream()");        ArrayList<String> daten = new ArrayList<>();        daten.add("Android");        daten.add("Java");        daten.add("SQL");        daten.add("Gradle");        Stream<String>  stream = daten.stream();        daten.add("DevOps");        daten.add("DevOps2");        stream.forEach(x-> System.out.println("x = " + x));                    }}